mapper.select:
    select m.id id,m.text text ,m.icon icon ,m.status status, m.description description, c.ctext ctext ,c.cicon cicon, c.href href ,c.cstatus cstatus
      from menus m,childmenus c, middle_rolemenu mr, role r
    where  c.menuid=m.id    and  r.id=mr.roleid and  mr.menuid=m.id and m.status='y' and c.cstatus='y'
    
    	
			and m.description = #{description,jdbcType=VARCHAR}
	 	
	 	 
			and r.id = #{rid,jdbcType=INTEGER}

mapper.update:
update menus
set text = #{text,jdbcType=VARCHAR},
  icon = #{icon,jdbcType=VARCHAR},
  status = #{status,jdbcType=VARCHAR},
  description = #{description,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
        select  from menus where 1=1 
         
			and id = #{id,jdbcType=INTEGER}
	 	
        
			and text LIKE CONCAT('%',#{text},'%') 
	 	
        
			and username like #{username,jdbcType=VARCHAR}
	 	
	 	
			and type = #{type,jdbcType=VARCHAR}
	 	
	 	
		 order by id desc limit #{startPos},#{limit}

        select count(*) from menus where 1=1 
        
			and id = #{id,jdbcType=INTEGER}
	 	
        
			and text LIKE CONCAT('%',#{text},'%') 
	 	
	 	
			and type = #{type,jdbcType=VARCHAR}
