mapper.select:
        select  from middle_rolemenu where 1=1 
         
			and  menuid = #{menuid,jdbcType=INTEGER}
	 	
	 	 
			and  roleid = #{roleid,jdbcType=INTEGER}
	 	
        
			and text LIKE CONCAT('%',#{text},'%') 
	 	
	 	
		 order by id desc limit #{startPos},#{limit}

        select count(*) from middle_rolemenu where 1=1 
        
			and id = #{id,jdbcType=INTEGER}
	 	
        
