mapper.insert:
insert into middleteacher (tid, cid, tuid, 
  tname, ttel, tstatus, 
  tcreatedate, temail, tdescription
  )
values (#{tid,jdbcType=INTEGER}, #{cid,jdbcType=INTEGER}, #{tuid,jdbcType=INTEGER}, 
  #{tname,jdbcType=VARCHAR}, #{ttel,jdbcType=VARCHAR}, #{tstatus,jdbcType=VARCHAR}, 
  #{tcreatedate,jdbcType=DATE}, #{temail,jdbcType=VARCHAR}, #{tdescription,jdbcType=VARCHAR}
  )

mapper.select:
        select  from middleteacher where 1=1 
      
			and id = #{id,jdbcType=INTEGER}
	 	
	 	
			and name  LIKE CONCAT('%',#{name},'%') 
	 	
	 	 
     and   cid = #{cid,jdbcType=INTEGER}
      
        
			and username like #{username,jdbcType=VARCHAR}
	 	
	 	
			and type = #{type,jdbcType=VARCHAR}
	 	
	 	
			limit #{startPos},#{limit}

        select count(*) from middleteacher where 1=1 
       
			and id = #{id,jdbcType=INTEGER}
	 	
	 	 
    	  and 	 cid = #{cid,jdbcType=INTEGER}
      	
	 	
			and name  LIKE CONCAT('%',#{name},'%') 
	 	
        
			and username like #{username,jdbcType=VARCHAR}
	 	
	 	
			and type = #{type,jdbcType=VARCHAR}
