mapper.insert:
insert into videocourse (id, name, status, 
  price, num, createdate, 
  videopath, descirption, type
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR}, 
  #{price,jdbcType=INTEGER}, #{num,jdbcType=INTEGER}, #{createdate,jdbcType=DATE}, 
  #{videopath,jdbcType=VARCHAR}, #{descirption,jdbcType=VARCHAR}, #{type,jdbcType=VARCHAR}
  )

mapper.select:
        select  from videocourse where 1=1 
       
			and id = #{id,jdbcType=INTEGER}
	 	
	 	
			and name  LIKE CONCAT('%',#{name},'%') 
	 	
	 	
			and type = #{type,jdbcType=VARCHAR}
	 	
	 	
			limit #{startPos},#{limit}

        select count(*) from videocourse where 1=1 
        
			and id = #{id,jdbcType=INTEGER}
	 	
	 	
			and name  LIKE CONCAT('%',#{name},'%') 
	 	
	 	
			and type = #{type,jdbcType=VARCHAR}
